INSERT INTO `text_assets` (`id`,`class_name`,`filename`,`content`,`created_at`,`updated_at`,`created_by_id`,`updated_by_id`,`lock_version`,`filter_id`) VALUES ('6','Javascript','site.js','$(document).ready(function(){  \r\n  $(\'ul.gallery\').galleria({\r\n    history   : false, \r\n    clickNext : false, \r\n    insert    : \'#display\',\r\n    onImage   : function(image,caption,thumb) { // let\'s add some image effects for demonstration purposes\r\n\r\n      // fade in the image & caption\r\n      image.css(\'display\',\'none\').fadeIn(1000);\r\n\r\n      // fetch the thumbnail container\r\n      var _li = thumb.parents(\'li\');\r\n\r\n      // fade out inactive thumbnail\r\n      _li.siblings().children(\'img.selected\').fadeTo(500,0.7);\r\n\r\n      // fade in active thumbnail\r\n      thumb.fadeTo(\'fast\',1).addClass(\'selected\');\r\n\r\n      // add a title for the clickable image\r\n        image.attr(\'title\',\'Next image >>\');\r\n    },\r\n    onThumb : function(thumb) { // thumbnail effects goes here\r\n\r\n      // fetch the thumbnail container\r\n      var _li = thumb.parents(\'li\');\r\n\r\n      // if thumbnail is active, fade all the way.\r\n      var _fadeTo = _li.is(\'.active\') ? \'1\' : \'0.7\';\r\n\r\n      // fade in the thumbnail when finnished loading\r\n      thumb.css({display:\'none\',opacity:_fadeTo}).fadeIn(1500);\r\n\r\n      // hover effects\r\n      thumb.hover(\r\n        function() { thumb.fadeTo(\'fast\',1); },\r\n        function() { _li.not(\'.active\').children(\'img\').fadeTo(\'fast\',0.7); } // don\'t fade out if the parent is active\r\n      )\r\n    }\r\n  });\r\n})','2008-07-11 11:01:31','2008-07-11 11:51:58','1','1','8',NULL);
